import React, { useState, useEffect, useRef } from 'react';
import { io, Socket } from 'socket.io-client';
import { useAuth } from '../context/AuthContext';
import { Send, Mic, Phone, Video } from 'lucide-react';
import api from '../services/api';

interface Message {
    _id: string;
    content: string;
    sender: {
        _id: string;
        name: string;
        avatar?: string;
    } | string; // string for 'bot'
    type: string;
    createdAt: string;
    metadata?: any;
}

interface ChatWindowProps {
    conversationId: string;
    onVideoCall?: () => void;
}

export const ChatWindow: React.FC<ChatWindowProps> = ({ conversationId, onVideoCall }) => {
    const [messages, setMessages] = useState<Message[]>([]);
    const [newMessage, setNewMessage] = useState('');
    const [socket, setSocket] = useState<Socket | null>(null);
    const { user, token } = useAuth();
    const messagesEndRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        // Initialize Socket.IO
        // Initialize Socket.IO
        const newSocket = io('http://localhost:5000', {
            auth: { token },
            transports: ['websocket']
        });

        setSocket(newSocket);

        // Load initial messages
        fetchMessages();

        // Socket event listeners
        newSocket.on('chat:message', (message: Message) => {
            setMessages(prev => [...prev, message]);
            scrollToBottom();
        });

        return () => {
            newSocket.disconnect();
        };
    }, [conversationId, token]);

    const fetchMessages = async () => {
        try {
            const response = await api.get(`/chat/conversations/${conversationId}/messages`);
            setMessages(response.data.reverse()); // Assuming API returns newest first
            scrollToBottom();
        } catch (error) {
            console.error('Failed to fetch messages', error);
        }
    };

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    const handleSendMessage = (e: React.FormEvent) => {
        e.preventDefault();
        if (!newMessage.trim() || !socket) return;

        const messageData = {
            conversationId,
            message: newMessage,
            type: 'text'
        };

        socket.emit('chat:message', messageData);
        setNewMessage('');
    };

    return (
        <div className="flex flex-col h-full bg-white rounded-xl shadow-sm border border-slate-200">
            {/* Header */}
            <div className="p-4 border-b border-slate-200 flex justify-between items-center">
                <h3 className="font-semibold text-slate-800">Chat</h3>
                <div className="flex space-x-2">
                    <button
                        onClick={onVideoCall}
                        className="p-2 hover:bg-slate-100 rounded-full text-slate-600 transition-colors"
                    >
                        <Video className="h-5 w-5" />
                    </button>
                    <button className="p-2 hover:bg-slate-100 rounded-full text-slate-600 transition-colors">
                        <Phone className="h-5 w-5" />
                    </button>
                </div>
            </div>

            {/* Messages Area */}
            <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {messages.map((msg) => {
                    const isMe = msg.sender && typeof msg.sender === 'object' ? msg.sender._id === user?.id : false;
                    const isBot = msg.sender === 'bot' || (msg.sender && typeof msg.sender === 'object' && msg.sender._id === 'bot');

                    return (
                        <div key={msg._id} className={`flex ${isMe ? 'justify-end' : 'justify-start'}`}>
                            <div
                                className={`max-w-[70%] rounded-2xl px-4 py-2 ${isMe
                                    ? 'bg-blue-500 text-white rounded-br-none'
                                    : isBot
                                        ? 'bg-green-100 text-green-900 rounded-bl-none'
                                        : 'bg-gray-100 text-gray-800 rounded-bl-none'
                                    }`}
                            >
                                <p>{msg.content}</p>
                                {msg.metadata?.suggestions && (
                                    <div className="mt-2 flex flex-wrap gap-2">
                                        {msg.metadata.suggestions.map((suggestion: string, idx: number) => (
                                            <button
                                                key={idx}
                                                onClick={() => setNewMessage(suggestion)}
                                                className="text-xs bg-white/50 hover:bg-white/80 px-2 py-1 rounded-full transition-colors"
                                            >
                                                {suggestion}
                                            </button>
                                        ))}
                                    </div>
                                )}
                            </div>
                        </div>
                    );
                })}
                <div ref={messagesEndRef} />
            </div>

            {/* Input Area */}
            <form onSubmit={handleSendMessage} className="p-4 border-t border-slate-200 flex space-x-2">
                <button type="button" className="p-2 hover:bg-slate-100 rounded-full text-slate-500">
                    <Mic className="h-5 w-5" />
                </button>
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Type a message..."
                    className="flex-1 px-4 py-2 border border-slate-300 rounded-full focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                />
                <button
                    type="submit"
                    disabled={!newMessage.trim()}
                    className="p-2 bg-primary-600 text-white rounded-full hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                    <Send className="h-5 w-5" />
                </button>
            </form>
        </div>
    );
};

import React, { useState, useEffect, useRef } from 'react';
import { MessageCircle, X, Send, Minimize2, Maximize2, Bot } from 'lucide-react';
import api from '../services/api';
import { useAuth } from '../context/AuthContext';

interface Message {
    _id: string;
    content: string;
    sender: { _id: string; name: string };
    createdAt: string;
}

export const FloatingChatWidget: React.FC = () => {
    const { user } = useAuth();
    const [isOpen, setIsOpen] = useState(false);
    const [isExpanded, setIsExpanded] = useState(false);
    const [messages, setMessages] = useState<Message[]>([]);
    const [newMessage, setNewMessage] = useState('');
    const [conversationId, setConversationId] = useState<string | null>(null);
    const [isTyping, setIsTyping] = useState(false);

    // Auto-scroll ref
    const messagesEndRef = useRef<HTMLDivElement>(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages, isTyping, isOpen]);

    useEffect(() => {
        if (isOpen && !conversationId) {
            initializeBotChat();
        }
    }, [isOpen]);

    const initializeBotChat = async () => {
        try {
            // Check for existing bot conversation or create one
            const response = await api.post('/chat/conversations', {
                participants: ['bot'],
                type: 'direct'
            });
            setConversationId(response.data._id);
            // Fetch recent messages
            if (response.data._id) {
                const msgResponse = await api.get(`/chat/conversations/${response.data._id}/messages`);
                // Backend returns Newest First, we want Oldest First (Chronological)
                setMessages(msgResponse.data.reverse());
            }
        } catch (error) {
            console.error('Failed to init bot chat', error);
        }
    };

    const handleSendMessage = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!newMessage.trim() || !conversationId) return;

        try {
            const content = newMessage;
            setNewMessage('');

            // Optimistic update
            const tempMsg: Message = {
                _id: Date.now().toString(),
                content,
                sender: { _id: (user as any)?._id || 'me', name: user?.name || 'Me' },
                createdAt: new Date().toISOString()
            };
            setMessages(prev => [...prev, tempMsg]);
            setIsTyping(true); // Start typing indicator

            await api.post(`/chat/conversations/${conversationId}/messages`, { content });

            // Poll for bot response (attempt every 2s for 30s)
            let attempts = 0;
            const maxAttempts = 15;

            const pollInterval = setInterval(async () => {
                attempts++;
                try {
                    const msgResponse = await api.get(`/chat/conversations/${conversationId}/messages`);
                    // Reverse to get Chronological order
                    const newMessages = msgResponse.data.reverse();

                    // Check if there's a new message from the bot
                    // Last message in chronological list should be the newest
                    const lastMsg = newMessages[newMessages.length - 1];

                    // Safe check for sender (bot has no sender or sender is null)
                    const isBot = !lastMsg.sender || lastMsg.sender === null;
                    const isOtherUser = lastMsg.sender?._id !== (user as any)?._id && lastMsg.sender?._id !== 'me';

                    if (lastMsg && (isBot || isOtherUser)) {
                        setMessages(newMessages);
                        setIsTyping(false); // Stop typing indicator
                        clearInterval(pollInterval);
                    } else if (attempts >= maxAttempts) {
                        setIsTyping(false); // Stop typing indicator on timeout
                        clearInterval(pollInterval);
                    }
                } catch (err) {
                    console.error('Polling error', err);
                }
            }, 2000);

        } catch (error) {
            console.error('Failed to send message', error);
        }
    };

    if (!isOpen) {
        return (
            <button
                onClick={() => setIsOpen(true)}
                className="fixed bottom-6 right-6 w-14 h-14 bg-indigo-600 text-white rounded-full shadow-lg hover:bg-indigo-700 transition-all flex items-center justify-center z-50 hover:scale-110"
            >
                <MessageCircle className="w-8 h-8" />
            </button>
        );
    }

    return (
        <div className={`fixed bottom-6 right-6 bg-white rounded-2xl shadow-2xl border border-slate-200 flex flex-col z-50 transition-all duration-300 ${isExpanded ? 'w-96 h-[600px]' : 'w-80 h-[500px]'}`}>
            {/* Header */}
            <div className="p-4 bg-indigo-600 text-white rounded-t-2xl flex justify-between items-center shadow-md">
                <div className="flex items-center gap-2">
                    <div className="relative">
                        <Bot className="w-6 h-6" />
                        <div className="absolute -bottom-1 -right-1 w-2.5 h-2.5 bg-green-400 rounded-full border-2 border-indigo-600 animate-pulse" />
                    </div>
                    <span className="font-bold">Mindora</span>
                </div>
                <div className="flex items-center gap-2">
                    <button onClick={() => setIsExpanded(!isExpanded)} className="p-1 hover:bg-indigo-500 rounded transition-colors">
                        {isExpanded ? <Minimize2 className="w-4 h-4" /> : <Maximize2 className="w-4 h-4" />}
                    </button>
                    <button onClick={() => setIsOpen(false)} className="p-1 hover:bg-indigo-500 rounded transition-colors">
                        <X className="w-5 h-5" />
                    </button>
                </div>
            </div>

            {/* Messages */}
            <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-slate-50 scrollbar-thin scrollbar-thumb-slate-200">
                {messages.map((msg) => {
                    const isMe = msg.sender?._id === (user as any)?._id || msg.sender?._id === 'me';
                    return (
                        <div key={msg._id} className={`flex ${isMe ? 'justify-end' : 'justify-start'}`}>
                            {!isMe && (
                                <div className="w-8 h-8 rounded-full bg-green-100 flex items-center justify-center mr-2 self-end mb-1">
                                    <Bot className="w-5 h-5 text-green-600" />
                                </div>
                            )}
                            <div className={`max-w-[75%] p-3.5 rounded-2xl text-sm shadow-sm leading-relaxed ${isMe
                                ? 'bg-blue-500 text-white rounded-tr-none'
                                : 'bg-green-100 text-green-900 rounded-tl-none'
                                }`}>
                                {msg.content}
                            </div>
                        </div>
                    );
                })}
                {isTyping && (
                    <div className="flex justify-start items-end">
                        <div className="w-8 h-8 rounded-full bg-green-100 flex items-center justify-center mr-2 mb-1">
                            <Bot className="w-5 h-5 text-green-600" />
                        </div>
                        <div className="bg-green-100 text-green-900 border border-green-200 p-4 rounded-2xl rounded-tl-none shadow-sm flex items-center space-x-1.5 h-12">
                            <div className="w-1.5 h-1.5 bg-green-400 rounded-full animate-bounce" style={{ animationDelay: '0ms' }} />
                            <div className="w-1.5 h-1.5 bg-green-400 rounded-full animate-bounce" style={{ animationDelay: '150ms' }} />
                            <div className="w-1.5 h-1.5 bg-green-400 rounded-full animate-bounce" style={{ animationDelay: '300ms' }} />
                        </div>
                    </div>
                )}
                {messages.length === 0 && (
                    <div className="flex flex-col items-center justify-center h-full text-center p-6 text-slate-500 space-y-3 opacity-60">
                        <Bot className="w-12 h-12 text-slate-300" />
                        <p className="text-sm">Hi! I'm Mindora. I'm here to listen and support you. How are you feeling today?</p>
                    </div>
                )}
                <div ref={messagesEndRef} />
            </div>

            {/* Input */}
            <form onSubmit={handleSendMessage} className="p-3 border-t border-slate-100 bg-white rounded-b-2xl">
                <div className="flex gap-2">
                    <input
                        type="text"
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        placeholder="Type a message..."
                        className="flex-1 px-4 py-2.5 bg-slate-50 border border-slate-200 rounded-full text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/20 focus:border-indigo-500 transition-all placeholder:text-slate-400"
                    />
                    <button
                        type="submit"
                        disabled={!newMessage.trim()}
                        className="p-2.5 bg-indigo-600 text-white rounded-full hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all transform hover:scale-105 active:scale-95 shadow-sm"
                    >
                        <Send className="w-5 h-5" />
                    </button>
                </div>
            </form>
        </div>
    );
};

import React, { useState, useEffect } from 'react';
import { User, Activity } from 'lucide-react';
import api from '../services/api';

interface Patient {
    id: string;
    name: string;
    lastSession: string;
    status: 'Active' | 'Inactive';
}

// Mock data for assigned patients
const MOCK_PATIENTS: Patient[] = [
    { id: 'test-user-id', name: 'Test User 4', lastSession: '2023-10-25', status: 'Active' },
    { id: 'p2', name: 'Sarah Connor', lastSession: '2023-10-20', status: 'Active' },
    { id: 'p3', name: 'John Doe', lastSession: '2023-10-15', status: 'Inactive' },
];

export const TherapistDashboard: React.FC = () => {
    const [selectedPatient, setSelectedPatient] = useState<Patient | null>(null);
    const [moodHistory, setMoodHistory] = useState<any[]>([]);

    useEffect(() => {
        if (selectedPatient) {
            const fetchHistory = async () => {
                try {
                    const history = await api.getMoodHistory(selectedPatient.id);
                    setMoodHistory(history);
                } catch (error) {
                    console.error('Failed to fetch mood history', error);
                    // Fallback for demo if backend is empty/error
                    setMoodHistory([
                        { id: '1', mood: 'Good', timestamp: new Date().toISOString() },
                        { id: '2', mood: 'Low', timestamp: new Date(Date.now() - 86400000).toISOString() }
                    ]);
                }
            };
            fetchHistory();
        }
    }, [selectedPatient]);

    return (
        <div className="p-8 max-w-7xl mx-auto space-y-8 animate-fade-in">
            <header className="mb-8">
                <h1 className="text-3xl font-bold text-slate-800">Therapist Dashboard</h1>
                <p className="text-slate-500">Manage your patients and track their progress.</p>
            </header>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">

                {/* Patient List */}
                <div className="bg-white rounded-2xl shadow-sm border border-slate-200 p-6 h-[600px] overflow-y-auto">
                    <h2 className="text-xl font-bold text-slate-800 mb-6 flex items-center gap-2">
                        <User className="w-5 h-5 text-teal-600" />
                        Assigned Patients
                    </h2>
                    <div className="space-y-4">
                        {MOCK_PATIENTS.map((patient) => (
                            <div
                                key={patient.id}
                                onClick={() => setSelectedPatient(patient)}
                                className={`p-4 rounded-xl border cursor-pointer transition-all ${selectedPatient?.id === patient.id
                                    ? 'border-teal-500 bg-teal-50 shadow-md'
                                    : 'border-slate-200 hover:border-teal-300 hover:bg-slate-50'
                                    }`}
                            >
                                <div className="flex justify-between items-start">
                                    <div>
                                        <h3 className="font-bold text-slate-800">{patient.name}</h3>
                                        <p className="text-sm text-slate-500">Last Session: {patient.lastSession}</p>
                                    </div>
                                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${patient.status === 'Active' ? 'bg-green-100 text-green-700' : 'bg-slate-100 text-slate-600'
                                        }`}>
                                        {patient.status}
                                    </span>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Patient Details & Mood History */}
                <div className="lg:col-span-2 space-y-8">
                    {selectedPatient ? (
                        <>
                            <div className="bg-white rounded-2xl shadow-sm border border-slate-200 p-8">
                                <div className="flex justify-between items-center mb-6">
                                    <h2 className="text-2xl font-bold text-slate-800">{selectedPatient.name}'s Overview</h2>
                                    <button className="px-4 py-2 bg-teal-600 text-white rounded-lg hover:bg-teal-700 transition-colors">
                                        Schedule Session
                                    </button>
                                </div>

                                <h3 className="text-lg font-bold text-slate-700 mb-4 flex items-center gap-2">
                                    <Activity className="w-5 h-5 text-blue-600" />
                                    Mood History
                                </h3>

                                {moodHistory.length > 0 ? (
                                    <div className="space-y-4">
                                        {moodHistory.map((log) => (
                                            <div key={log.id} className="flex items-center gap-4 p-4 bg-slate-50 rounded-xl border border-slate-100">
                                                <div className={`w-12 h-12 rounded-full flex items-center justify-center text-2xl ${log.mood === 'Great' ? 'bg-green-100' :
                                                    log.mood === 'Good' ? 'bg-blue-100' :
                                                        log.mood === 'Okay' ? 'bg-slate-200' :
                                                            log.mood === 'Low' ? 'bg-orange-100' : 'bg-red-100'
                                                    }`}>
                                                    {log.mood === 'Great' ? 'üòÑ' :
                                                        log.mood === 'Good' ? 'üôÇ' :
                                                            log.mood === 'Okay' ? 'üòê' :
                                                                log.mood === 'Low' ? 'üòî' : 'üò´'}
                                                </div>
                                                <div>
                                                    <p className="font-bold text-slate-800">{log.mood}</p>
                                                    <p className="text-sm text-slate-500">
                                                        {new Date(log.timestamp).toLocaleDateString()} at {new Date(log.timestamp).toLocaleTimeString()}
                                                    </p>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                ) : (
                                    <p className="text-slate-500 italic">No mood logs available for this patient.</p>
                                )}
                            </div>
                        </>
                    ) : (
                        <div className="h-full flex flex-col items-center justify-center text-slate-400 p-12 border-2 border-dashed border-slate-200 rounded-2xl">
                            <User className="w-16 h-16 mb-4 opacity-50" />
                            <p className="text-lg">Select a patient to view their details and mood history.</p>
                        </div>
                    )}
                </div>

            </div>
        </div>
    );
};

import React, { useState } from 'react';
import type { Therapist } from '../types';
import { Star, Clock, MapPin, Shield, MessageSquare, Video } from 'lucide-react';
import api from '../services/api';

interface TherapistProfileProps {
    therapist: Therapist;
    onBack: () => void;
    onChat: () => void;
    onBookingComplete: () => void;
}

export const TherapistProfile: React.FC<TherapistProfileProps> = ({ therapist, onBack, onChat, onBookingComplete }) => {
    const [bookingDate, setBookingDate] = useState('');
    const [bookingTime, setBookingTime] = useState('');
    const [isBooking, setIsBooking] = useState(false);

    const handleBookAppointment = async () => {
        if (!bookingDate || !bookingTime) {
            alert('Please select a date and time');
            return;
        }

        setIsBooking(true);
        try {
            await api.bookAppointment({
                therapistId: (therapist as any).id || (therapist as any)._id,
                date: bookingDate,
                time: bookingTime,
                type: 'video'
            });
            alert('Appointment booked successfully! You can join the video call from the Appointments tab.');
            onBookingComplete();
            onBack(); // Go back to list or maybe redirect to appointments tab
        } catch (error) {
            console.error('Booking failed', error);
            alert('Failed to book appointment');
        } finally {
            setIsBooking(false);
        }
    };

    return (
        <div className="h-full bg-white overflow-y-auto animate-fade-in">
            {/* Header Image */}
            <div className="h-48 bg-gradient-to-r from-teal-500 to-blue-600 relative">
                <button
                    onClick={onBack}
                    className="absolute top-4 left-4 bg-white/20 backdrop-blur-md p-2 rounded-full text-white hover:bg-white/30 transition-colors"
                >
                    ‚Üê Back
                </button>
            </div>

            <div className="max-w-4xl mx-auto px-6 -mt-16 relative z-10 pb-12">
                <div className="flex flex-col md:flex-row gap-6 items-start">
                    {/* Profile Image */}
                    <img
                        src={therapist.imageUrl}
                        alt={therapist.name}
                        className="w-32 h-32 rounded-2xl border-4 border-white shadow-lg object-cover bg-white"
                    />

                    <div className="flex-1 pt-16 md:pt-0 mt-2">
                        <div className="flex justify-between items-start">
                            <div>
                                <h1 className="text-3xl font-bold text-slate-900">{therapist.name}</h1>
