import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { ChatWindow } from '../components/ChatWindow';
import { TherapistList } from '../components/TherapistList';
import { TherapistProfile } from '../components/TherapistProfile';
import { InspirationPage } from './InspirationPage';
import { ProfilePage } from './ProfilePage';
import api from '../services/api';
import { LogOut, MessageSquare, Calendar, User, Home, TrendingUp, BookOpen, Bot } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

export const Dashboard: React.FC = () => {
    const { user, logout } = useAuth();
    const navigate = useNavigate();
    const [conversations, setConversations] = useState<any[]>([]);
    const [selectedConversationId, setSelectedConversationId] = useState<string | null>(null);
    const [activeTab, setActiveTab] = useState<'home' | 'chat' | 'appointments' | 'profile'>('home');

    useEffect(() => {
        if (user?.role === 'therapist') {
            navigate('/therapist/dashboard', { replace: true });
            return;
        }
        fetchConversations();
        fetchAppointments();
    }, [user, navigate]);

    const [, setAppointments] = useState<any[]>([]);

    const fetchAppointments = async () => {
        try {
            const data = await api.getAppointments();
            setAppointments(data);
        } catch (error) {
            console.error('Failed to fetch appointments', error);
        }
    };

    const fetchConversations = async () => {
        try {
            const response = await api.get('/chat/conversations');
            // Filter out AI Bot conversations (participants include 'bot')
            const humanConversations = response.data.filter((c: any) =>
                !c.participants.some((p: any) => p === 'bot' || p?.name === 'bot' || p?._id === 'bot')
            );
            setConversations(humanConversations);
            if (humanConversations.length > 0 && !selectedConversationId) {
                setSelectedConversationId(humanConversations[0]._id);
            }
        } catch (error) {
            console.error('Failed to fetch conversations', error);
        }
    };

    const [selectedTherapist, setSelectedTherapist] = useState<any | null>(null);

    const handleChatWithTherapist = async (therapistId: string) => {
        try {
            // Check if conversation already exists
            const existingConv = conversations.find(c =>
                c.participants.some((p: any) => (p._id === therapistId || p === therapistId))
            );

            if (existingConv) {
                setSelectedConversationId(existingConv._id);
            } else {
                // Create new conversation
                const response = await api.post('/chat/conversations', {
                    participants: [therapistId],
                    type: 'direct'
                });
                setConversations(prev => [response.data, ...prev]);
                setSelectedConversationId(response.data._id);
            }

            // Switch to chat tab
            setActiveTab('chat');
            setSelectedTherapist(null); // Close profile
        } catch (error) {
            console.error('Failed to start chat', error);
        }
    };

    return (
        <div className="flex h-screen bg-slate-50 overflow-hidden">
            {/* Sidebar */}
            <div className="w-64 bg-white border-r border-slate-200 flex flex-col">
                <div className="p-6">
                    <h1 className="text-2xl font-bold text-primary-600 flex items-center gap-2">
                        Mindora
                    </h1>
                </div>

                <nav className="flex-1 px-4 space-y-2">
                    <button
                        onClick={() => setActiveTab('home')}
                        className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${activeTab === 'home' ? 'bg-primary-50 text-primary-700' : 'text-slate-600 hover:bg-slate-50'
                            }`}
                    >
                        <Home className="h-5 w-5" />
                        <span className="font-medium">Home</span>
                    </button>
                    <button
                        onClick={() => navigate('/messages')}
                        className="w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors text-slate-600 hover:bg-slate-50"
                    >
                        <MessageSquare className="h-5 w-5" />
                        <span className="font-medium">Messages</span>
                    </button>
                    <button
                        onClick={() => navigate('/appointments')}
                        className="w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors text-slate-600 hover:bg-slate-50"
                    >
                        <Calendar className="h-5 w-5" />
                        <span className="font-medium">Appointments</span>
                    </button>
                    <button
                        onClick={() => navigate('/chatbot')}
                        className="w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors text-slate-600 hover:bg-slate-50"
                    >
                        <Bot className="h-5 w-5" />
                        <span className="font-medium">AI Assistant</span>
                    </button>
                    <button
                        onClick={() => navigate('/progress')}
                        className="w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors text-slate-600 hover:bg-slate-50"
                    >
                        <TrendingUp className="h-5 w-5" />
                        <span className="font-medium">My Progress</span>
                    </button>
                    <button
                        onClick={() => navigate('/resources')}
                        className="w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors text-slate-600 hover:bg-slate-50"
                    >
                        <BookOpen className="h-5 w-5" />
                        <span className="font-medium">Resources</span>
                    </button>
                    <button
                        onClick={() => setActiveTab('profile')}
                        className={`w-full flex items-center space-x-3 px-4 py-3 rounded-lg transition-colors ${activeTab === 'profile' ? 'bg-primary-50 text-primary-700' : 'text-slate-600 hover:bg-slate-50'
                            }`}
                    >
                        <User className="h-5 w-5" />
                        <span className="font-medium">Profile</span>
                    </button>
                </nav>

                <div className="p-4 border-t border-slate-200">
                    <div className="flex items-center space-x-3 mb-4">
                        <div className="h-10 w-10 rounded-full bg-primary-100 flex items-center justify-center text-primary-700 font-bold">
                            {user?.name?.charAt(0) || 'U'}
                        </div>
                        <div className="flex-1 min-w-0">
                            <p className="text-sm font-medium text-slate-900 truncate">{user?.name}</p>
                            <p className="text-xs text-slate-500 truncate">{user?.role}</p>
                        </div>
                    </div>
                    <button
                        onClick={() => {
                            logout();
                            window.location.href = '/';
                        }}
                        className="w-full flex items-center justify-center space-x-2 px-4 py-2 border border-slate-300 rounded-lg text-sm font-medium text-slate-700 hover:bg-slate-50 transition-colors"
                    >
                        <LogOut className="h-4 w-4" />
                        <span>Sign out</span>
                    </button>
                </div>
            </div>

            {/* Main Content */}
            <main className="flex-1 flex flex-col min-w-0 overflow-hidden">
                {activeTab === 'home' && <InspirationPage />}

                {activeTab === 'chat' && (
                    <div className="flex-1 flex">
                        {/* Conversation List */}
                        <div className="w-80 bg-white border-r border-slate-200 flex flex-col">
                            <div className="p-4 border-b border-slate-200">
                                <h3 className="font-bold text-slate-700">Your Conversations</h3>
                            </div>
                            <div className="flex-1 overflow-y-auto">
                                {conversations.map(conv => (
                                    <button
                                        key={conv._id}
                                        onClick={() => setSelectedConversationId(conv._id)}
                                        className={`w-full p-4 text-left border-b border-slate-100 hover:bg-slate-50 transition-colors ${selectedConversationId === conv._id ? 'bg-primary-50' : ''
                                            }`}
                                    >
                                        <div className="flex justify-between items-start">
                                            <h4 className="font-medium text-slate-900 truncate">
                                                {conv.type === 'group' ? conv.groupName : 'Direct Chat'}
                                            </h4>
                                            <span className="text-xs text-slate-500">
                                                {new Date(conv.updatedAt).toLocaleDateString()}
                                            </span>
                                        </div>
                                        <p className="text-sm text-slate-500 truncate mt-1">
                                            {conv.lastMessage?.content || 'No messages yet'}
                                        </p>
                                    </button>
                                ))}
                            </div>
                        </div>

                        {/* Chat Area */}
                        <div className="flex-1 bg-slate-50 p-4">
                            {selectedConversationId ? (
                                <ChatWindow conversationId={selectedConversationId} />
                            ) : (
                                <div className="h-full flex items-center justify-center text-slate-500">
                                    Select a conversation to start chatting
                                </div>
                            )}
                        </div>
                    </div>
                )}

                {activeTab === 'appointments' && (
                    <div className="p-8 overflow-y-auto h-full">
                        {selectedTherapist ? (
                            <TherapistProfile
                                therapist={selectedTherapist}
                                onBack={() => setSelectedTherapist(null)}
                                onChat={() => handleChatWithTherapist(selectedTherapist._id)}
                                onBookingComplete={fetchAppointments}
                            />
                        ) : (
                            <>
                                <div className="flex justify-between items-center mb-6">
                                    <h2 className="text-2xl font-bold text-slate-900">Find a Specialist</h2>
                                    <div className="flex gap-2">
                                        <select className="border border-slate-300 rounded-lg px-3 py-2 text-sm">
                                            <option>All Specializations</option>
                                            <option>Anxiety</option>
                                            <option>Depression</option>
                                            <option>Couples Therapy</option>
                                        </select>
                                    </div>
                                </div>
                                <TherapistList onSelect={setSelectedTherapist} />
                            </>
                        )}
                    </div>
                )}

                {activeTab === 'profile' && <ProfilePage />}
            </main>
        </div>
    );
};

import React, { useState, useEffect } from 'react';
import { Wind, Sun, Moon, Cloud, Quote, ArrowRight } from 'lucide-react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';


const QUOTES = [
    { text: "Peace comes from within. Do not seek it without.", author: "Buddha" },
    { text: "The best way to predict the future is to create it.", author: "Peter Drucker" },
    { text: "You are enough just as you are.", author: "Meghan Markle" },
    { text: "Breath is the bridge which connects life to consciousness.", author: "Thich Nhat Hanh" },
    { text: "Every moment is a fresh beginning.", author: "T.S. Eliot" },
    { text: "Believe you can and you're halfway there.", author: "Theodore Roosevelt" },
    { text: "Act as if what you do makes a difference. It does.", author: "William James" },
    { text: "Happiness depends upon ourselves.", author: "Aristotle" }
];

export const InspirationPage: React.FC = () => {
    const { user } = useAuth();
    const navigate = useNavigate();
    const [quote, setQuote] = useState(QUOTES[0]);
    const [greeting, setGreeting] = useState('');
    const [selectedMood, setSelectedMood] = useState<string | null>(null);

    useEffect(() => {
        // Set random quote
        const randomIndex = Math.floor(Math.random() * QUOTES.length);
        setQuote(QUOTES[randomIndex]);

        // Set greeting based on time
        const hour = new Date().getHours();
        if (hour < 12) setGreeting('Good Morning');
        else if (hour < 18) setGreeting('Good Afternoon');
        else setGreeting('Good Evening');

        return () => { };
    }, []);

    return (
        <div className="h-full w-full overflow-y-auto bg-gradient-to-br from-teal-50 via-blue-50 to-indigo-50 p-8">
            <div className="max-w-4xl mx-auto space-y-12">

                {/* Header Section */}
                <header className="text-center space-y-4 animate-fade-in">
                    <div className="flex justify-center mb-4">
                        {greeting.includes('Morning') ? (
                            <Sun className="w-12 h-12 text-amber-400" />
                        ) : greeting.includes('Afternoon') ? (
                            <Cloud className="w-12 h-12 text-blue-400" />
                        ) : (
                            <Moon className="w-12 h-12 text-indigo-400" />
                        )}
                    </div>
                    <h1 className="text-4xl md:text-5xl font-serif text-slate-800 tracking-tight">
                        {greeting}, {user?.name?.split(' ')[0] || 'Friend'}
                    </h1>
                    <p className="text-slate-500 text-lg">
                        {new Date().toLocaleDateString('en-US', { weekday: 'long', month: 'long', day: 'numeric' })}
                    </p>
                </header>

                {/* Quote Card */}
                <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-8 md:p-12 shadow-xl border border-white/50 relative overflow-hidden transition-all hover:shadow-2xl hover:bg-white/90">
                    <Quote className="absolute top-6 left-6 w-12 h-12 text-teal-100 -z-10" />
                    <figure className="text-center space-y-6">
                        <blockquote className="text-2xl md:text-3xl font-light text-slate-700 leading-relaxed italic">
                            "{quote.text}"
                        </blockquote>
                        <figcaption className="text-teal-600 font-medium tracking-wide uppercase text-sm">
                            ‚Äî {quote.author}
                        </figcaption>
                    </figure>
                </div>

                {/* Breathing Exercise Link */}
                <div
                    onClick={() => navigate('/breathing')}
                    className="bg-gradient-to-r from-teal-500 to-blue-500 rounded-2xl p-8 shadow-lg text-white cursor-pointer hover:shadow-xl hover:scale-[1.02] transition-all group"
                >
                    <div className="flex items-center justify-between">
                        <div className="space-y-2">
                            <div className="flex items-center space-x-2">
                                <Wind className="w-6 h-6 text-teal-100" />
                                <h3 className="font-bold text-xl tracking-wide">Breathing Space</h3>
                            </div>
                            <p className="text-teal-50 max-w-md">
                                Discover professional breathing techniques like Box Breathing and 4-7-8 Relaxation to calm your mind.
                            </p>
                        </div>
                        <div className="bg-white/20 p-3 rounded-full group-hover:bg-white/30 transition-colors">
                            <ArrowRight className="w-6 h-6" />
                        </div>
                    </div>
                </div>

                {/* Mood Check-in Widget */}
                <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-8 shadow-lg border border-white/50">
                    <h3 className="text-center text-slate-600 font-medium uppercase tracking-widest text-sm mb-8">How are you feeling today?</h3>

                    <div className="flex flex-wrap justify-center gap-4 md:gap-8">
                        {[
                            { label: 'Great', emoji: 'üòÑ', color: 'bg-green-100 text-green-600 hover:bg-green-200', ring: 'ring-green-400' },
                            { label: 'Good', emoji: 'üôÇ', color: 'bg-blue-100 text-blue-600 hover:bg-blue-200', ring: 'ring-blue-400' },
                            { label: 'Okay', emoji: 'üòê', color: 'bg-slate-100 text-slate-600 hover:bg-slate-200', ring: 'ring-slate-400' },
                            { label: 'Low', emoji: 'üòî', color: 'bg-orange-100 text-orange-600 hover:bg-orange-200', ring: 'ring-orange-400' },
                            { label: 'Bad', emoji: 'üò´', color: 'bg-red-100 text-red-600 hover:bg-red-200', ring: 'ring-red-400' },
                        ].map((mood) => (
                            <button
                                key={mood.label}
                                onClick={() => {
                                    setSelectedMood(mood.label);
                                    // In a real app, this would save to the backend
                                    console.log(`Mood selected: ${mood.label}`);
                                }}
                                className={`flex flex-col items-center gap-2 p-4 rounded-xl transition-all transform hover:scale-110 ${mood.color} ${selectedMood === mood.label ? `ring-4 ${mood.ring} scale-110 shadow-lg` : 'opacity-80 hover:opacity-100'
                                    }`}
                            >
                                <span className="text-4xl">{mood.emoji}</span>
                                <span className="text-sm font-medium">{mood.label}</span>
                            </button>
                        ))}
                    </div>
                    {selectedMood && (
                        <p className="text-center text-slate-500 mt-6 animate-fade-in">
                            Thanks for checking in! We've logged that you're feeling <span className="font-medium text-slate-800">{selectedMood.toLowerCase()}</span>.
                        </p>
                    )}
                </div>

            </div>
        </div>
    );
};

import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import { Lock, Mail, Loader2 } from 'lucide-react';

export const LoginPage: React.FC = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const { login } = useAuth();
    const navigate = useNavigate();

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setIsLoading(true);

        try {
            const response = await api.post('/auth/login', { email, password });
            login(response.data.token, response.data.user);
            if (response.data.user.role === 'therapist') {
                navigate('/therapist/dashboard');
            } else {
                navigate('/dashboard');
            }
        } catch (err: any) {
            setError(err.response?.data?.error || 'Failed to login');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-slate-50">
            <div className="max-w-md w-full space-y-8 p-8 bg-white rounded-xl shadow-lg">
                <div className="text-center">
                    <h2 className="mt-6 text-3xl font-extrabold text-slate-900">
                        Welcome Back
                    </h2>
                    <p className="mt-2 text-sm text-slate-600">
                        Sign in to access your mental health dashboard
                    </p>
                </div>

                <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
                    {error && (
                        <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded-lg text-sm">
                            {error}
                        </div>
                    )}

                    <div className="rounded-md shadow-sm -space-y-px">
                        <div className="relative">
                            <Mail className="absolute left-3 top-3 h-5 w-5 text-slate-400" />
                            <input
                                type="email"
                                required
                                className="appearance-none rounded-t-md relative block w-full px-10 py-3 border border-slate-300 placeholder-slate-500 text-slate-900 focus:outline-none focus:ring-primary-500 focus:border-primary-500 focus:z-10 sm:text-sm"
                                placeholder="Email address"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                        </div>
                        <div className="relative">
                            <Lock className="absolute left-3 top-3 h-5 w-5 text-slate-400" />
                            <input
                                type="password"
                                required
                                className="appearance-none rounded-b-md relative block w-full px-10 py-3 border border-slate-300 placeholder-slate-500 text-slate-900 focus:outline-none focus:ring-primary-500 focus:border-primary-500 focus:z-10 sm:text-sm"
                                placeholder="Password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </div>
                    </div>

                    <div>
                        <button
                            type="submit"
                            disabled={isLoading}
                            className="group relative w-full flex justify-center py-3 px-4 border border-transparent text-sm font-medium rounded-lg text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                        >
                            {isLoading ? (
                                <Loader2 className="h-5 w-5 animate-spin" />
                            ) : (
                                'Sign in'
                            )}
                        </button>
                    </div>

                    <div className="text-sm text-center">
                        <span className="text-slate-600">Don't have an account? </span>
                        <Link to="/register" className="font-medium text-primary-600 hover:text-primary-500">
                            Sign up
                        </Link>
                    </div>
                </form>
            </div>
        </div>
    );
};

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Heart, Brain, Users, MessageCircle, Sparkles } from 'lucide-react';
import FingerprintJS from '@fingerprintjs/fingerprintjs';
import axios from 'axios';

type EmotionType = 'anxious' | 'sad' | 'stressed' | 'overwhelmed' | 'hopeful' | 'curious';

interface Emotion {
    id: EmotionType;
    label: string;
    icon: string;
    color: string;
}

const API_BASE_URL = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';

export const OnboardingPage: React.FC = () => {
    const navigate = useNavigate();
    const [step, setStep] = useState<1 | 2 | 3>(1);
    const [selectedEmotion, setSelectedEmotion] = useState<EmotionType | null>(null);
    const [userMessage, setUserMessage] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [deviceFingerprint, setDeviceFingerprint] = useState<string | null>(null);
    const [error, setError] = useState<string | null>(null);

    // Initialize device fingerprinting on mount
    useEffect(() => {
        const initFingerprint = async () => {
            try {
                const fp = await FingerprintJS.load();
                const result = await fp.get();
                setDeviceFingerprint(result.visitorId);
                console.log('Device fingerprint generated:', result.visitorId);
            } catch (err) {
                console.error('Error generating fingerprint:', err);
                // Fallback to a random ID if fingerprinting fails
                setDeviceFingerprint(`fallback_${Math.random().toString(36).substring(2, 15)}`);
            }
        };

        initFingerprint();
    }, []);

    const emotions: Emotion[] = [
        { id: 'anxious', label: 'Anxious', icon: 'üò∞', color: 'secondary' },
        { id: 'sad', label: 'Sad', icon: 'üòî', color: 'secondary' },
        { id: 'stressed', label: 'Stressed', icon: 'üòì', color: 'neutral' },
        { id: 'overwhelmed', label: 'Overwhelmed', icon: 'üòµ', color: 'neutral' },
        { id: 'hopeful', label: 'Hopeful', icon: 'üåü', color: 'primary' },
        { id: 'curious', label: 'Just Exploring', icon: 'ü§î', color: 'primary' },
    ];

    const handleEmotionSelect = (emotionId: EmotionType) => {
        setSelectedEmotion(emotionId);
        // Auto-advance to step 2 after 500ms
        setTimeout(() => setStep(2), 500);
    };

    const handleStartChat = async () => {
        setIsSubmitting(true);
        setError(null);

        if (!deviceFingerprint) {
            setError('Initializing fingerprint... Please wait.');
            setIsSubmitting(false);
            return;
        }

        if (!userMessage.trim()) {
            setError('Please enter a message before continuing.');
            setIsSubmitting(false);
            return;
        }

        try {
            // Call backend to create anonymous session
            const response = await axios.post(`${API_BASE_URL}/auth/anonymous`, {
                deviceFingerprint,
                initialEmotion: selectedEmotion,
                initialMessage: userMessage,
            });

            const { token, sessionId, temporaryName, maxFreeMessages } = response.data;

            // Store token in localStorage
            localStorage.setItem('anonymousSessionToken', token);
            localStorage.setItem('sessionId', sessionId);

            console.log('Anonymous session created:', {
                sessionId,
                temporaryName,
                maxFreeMessages,
            });

            // Navigate to chatbot with session data
            navigate('/chatbot', {
                state: {
                    isAnonymous: true,
                    sessionId,
                    temporaryName,
                    maxFreeMessages,
                    initialEmotion: selectedEmotion,
                    initialMessage: userMessage,
                },
            });
        } catch (err: any) {
            console.error('Error creating anonymous session:', err);

            // Handle specific error codes
            if (err.response) {
                const { status, data } = err.response;

                if (status === 503) {
                    setError('Our servers are currently unavailable. Please try again in a few moments.');
                } else if (status === 409) {
                    setError(data.message || 'A session already exists for this device. Redirecting...');
                    // Still navigate, the session exists
                    setTimeout(() => navigate('/chatbot'), 1500);
                } else if (status === 400) {
                    setError(data.message || 'Invalid request. Please refresh and try again.');
                } else {
                    setError('An unexpected error occurred. Please try again.');
                }
            } else if (err.request) {
                setError('Cannot connect to the server. Please check your internet connection.');
            } else {
                setError('An error occurred. Please try again.');
            }

            setIsSubmitting(false);
        }
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-primary-50 via-secondary-50 to-neutral-50 flex items-center justify-center p-4">
            <div className="w-full max-w-3xl">
                {/* Header - Always Visible */}
                <motion.div
                    className="text-center mb-12"
                    initial={{ opacity: 0, y: -20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.6 }}
                >
                    <div className="flex items-center justify-center mb-4">
                        <div className="bg-primary-200 p-3 rounded-full">
                            <Heart className="w-8 h-8 text-primary-700" />
                        </div>
                    </div>
                    <h1 className="text-4xl font-heading text-primary-900 mb-2">
                        You're Not Alone
                    </h1>
                    <p className="text-lg text-gray-600">
                        Take a deep breath. We're here to listen.
                    </p>
                </motion.div>

                {/* Step 1: Emotion Picker */}
                {step === 1 && (
                    <motion.div
                        initial={{ opacity: 0, scale: 0.95 }}
                        animate={{ opacity: 1, scale: 1 }}
                        transition={{ duration: 0.4 }}
                    >
                        <div className="card max-w-2xl mx-auto">
                            <h2 className="text-2xl font-heading text-primary-800 mb-6 text-center">
                                How are you feeling right now?
                            </h2>

                            <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                                {emotions.map((emotion) => (
                                    <motion.button
                                        key={emotion.id}
                                        onClick={() => handleEmotionSelect(emotion.id)}
                                        className={`
                      p-6 rounded-xl border-2 transition-all duration-300
                      ${selectedEmotion === emotion.id
                                                ? 'border-secondary-400 bg-secondary-50 shadow-lg'
                                                : 'border-primary-200 bg-white hover:border-primary-300 hover:shadow-md'
                                            }
                    `}
                                        whileHover={{ scale: 1.05 }}
                                        whileTap={{ scale: 0.95 }}
                                    >
                                        <div className="text-4xl mb-2">{emotion.icon}</div>
                                        <div className="text-base font-medium text-primary-800">
                                            {emotion.label}
                                        </div>
                                    </motion.button>
                                ))}
                            </div>

                            <p className="text-sm text-gray-500 text-center mt-6">
                                This helps us understand how to support you better
                            </p>
                        </div>
                    </motion.div>
                )}

                {/* Step 2: Free-Form Message */}
                {step === 2 && (
                    <motion.div
                        initial={{ opacity: 0, x: 20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ duration: 0.4 }}
                    >
                        <div className="card max-w-2xl mx-auto">
                            <h2 className="text-2xl font-heading text-primary-800 mb-6">
                                What brings you here today?
                            </h2>

                            <textarea
                                value={userMessage}
                                onChange={(e) => setUserMessage(e.target.value)}
                                placeholder="You can share as much or as little as you'd like. There's no judgment here."
                                className="input-field min-h-[180px] resize-none mb-4"
                                autoFocus
                            />

                            <div className="flex items-start gap-3 mb-6 p-4 bg-secondary-50 rounded-lg border border-secondary-200">
                                <Sparkles className="w-5 h-5 text-secondary-600 mt-0.5 flex-shrink-0" />
                                <p className="text-sm text-secondary-900">
                                    <strong>Just so you know:</strong> I'm an AI assistant here to support you, but I'm not a therapist.
                                    Everything we discuss is private, and I'm here to help connect you with the right resources.
                                </p>
                            </div>

                            {/* Error Message */}
                            {error && (
                                <motion.div
                                    className="mb-4 p-4 bg-danger-50 border border-danger-200 rounded-lg"
                                    initial={{ opacity: 0, y: -10 }}
                                    animate={{ opacity: 1, y: 0 }}
                                >
                                    <p className="text-sm text-danger-700">{error}</p>
                                </motion.div>
                            )}

                            <div className="flex gap-3">
                                <button
                                    onClick={() => setStep(1)}
                                    className="btn-secondary flex-1"
                                >
                                    Back
                                </button>
                                <button
                                    onClick={handleStartChat}
                                    disabled={!userMessage.trim() || isSubmitting || !deviceFingerprint}
                                    className="btn-primary flex-1"
                                >
                                    {isSubmitting ? (
                                        <span className="flex items-center justify-center gap-2">
                                            <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
                                            Starting...
                                        </span>
                                    ) : (
                                        <span className="flex items-center justify-center gap-2">
                                            <MessageCircle className="w-4 h-4" />
                                            Start Talking
                                        </span>
                                    )}
                                </button>
                            </div>
                        </div>
                    </motion.div>
                )}

                {/* Footer - Reassurance */}
                <motion.div
                    className="mt-12 text-center"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.5, duration: 0.6 }}
                >
                    <div className="flex items-center justify-center gap-6 text-sm text-gray-500">
                        <div className="flex items-center gap-2">
                            <Brain className="w-4 h-4 text-primary-500" />
                            <span>Confidential</span>
                        </div>
                        <div className="flex items-center gap-2">
                            <Users className="w-4 h-4 text-primary-500" />
                            <span>24/7 Support</span>
                        </div>
                        <div className="flex items-center gap-2">
                            <Heart className="w-4 h-4 text-primary-500" />
                            <span>No Judgment</span>
                        </div>
                    </div>

                    <p className="text-xs text-gray-400 mt-6">
                        üîí No sign-up required. Start anonymous, create an account later.
                    </p>
                </motion.div>
            </div>
        </div>
    );
};

