import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Search, Send, Users, User, MoreVertical, Smile } from 'lucide-react';

interface Message {
    id: string;
    senderId: string;
    senderName: string;
    content: string;
    timestamp: Date;
    isOwn: boolean;
}

interface Chat {
    id: string;
    type: 'direct' | 'group';
    name: string;
    lastMessage: string;
    lastMessageTime: Date;
    unreadCount: number;
    avatar?: string;
    participants?: string[];
}

export const PatientMessagesPage: React.FC = () => {
    const [activeChat, setActiveChat] = useState<string>('chat1');
    const [newMessage, setNewMessage] = useState('');

    // Mock chats
    const chats: Chat[] = [
        {
            id: 'chat1',
            type: 'direct',
            name: 'Dr. Sarah Mehta',
            lastMessage: 'Great progress this week!',
            lastMessageTime: new Date(Date.now() - 3600000),
            unreadCount: 2,
            avatar: 'https://ui-avatars.com/api/?name=Sarah+Mehta&background=3b82f6&color=fff&size=100'
        },
        {
            id: 'chat2',
            type: 'direct',
            name: 'Dr. Rajesh Kumar',
            lastMessage: "Let's schedule our next session",
            lastMessageTime: new Date(Date.now() - 86400000),
            unreadCount: 0,
            avatar: 'https://ui-avatars.com/api/?name=Rajesh+Kumar&background=8b5cf6&color=fff&size=100'
        },
        {
            id: 'group1',
            type: 'group',
            name: 'Anxiety Support Circle',
            lastMessage: 'Maya: Thanks for sharing, really helps!',
            lastMessageTime: new Date(Date.now() - 7200000),
            unreadCount: 5,
            participants: ['You', 'Maya', 'Arjun', 'Priya', 'Dr. Neha']
        },
        {
            id: 'group2',
            type: 'group',
            name: 'Mindfulness Practice Group',
            lastMessage: 'Guided meditation session tomorrow at 7 PM',
            lastMessageTime: new Date(Date.now() - 172800000),
            unreadCount: 0,
            participants: ['You', 'Karan', 'Anjali', 'Rohit']
        }
    ];

    // Mock messages for active chat
    const getMockMessages = (chatId: string): Message[] => {
        if (chatId === 'chat1') {
            return [
                {
                    id: 'm1',
                    senderId: 'therapist1',
                    senderName: 'Dr. Sarah Mehta',
                    content: 'Hi! How have you been feeling since our last session?',
                    timestamp: new Date(Date.now() - 7200000),
                    isOwn: false
                },
                {
                    id: 'm2',
                    senderId: 'me',
                    senderName: 'You',
                    content: 'Much better! The breathing exercises really helped during my anxiety attack yesterday.',
                    timestamp: new Date(Date.now() - 7000000),
                    isOwn: true
                },
                {
                    id: 'm3',
                    senderId: 'therapist1',
                    senderName: 'Dr. Sarah Mehta',
                    content: "That's wonderful to hear! Remember to practice them daily, not just during attacks. It helps build resilience.",
                    timestamp: new Date(Date.now() - 3600000),
                    isOwn: false
                },
                {
                    id: 'm4',
                    senderId: 'therapist1',
                    senderName: 'Dr. Sarah Mehta',
                    content: 'Great progress this week!',
                    timestamp: new Date(Date.now() - 1800000),
                    isOwn: false
                }
            ];
        } else if (chatId === 'group1') {
            return [
                {
                    id: 'g1',
                    senderId: 'user1',
                    senderName: 'Maya',
                    content: "Hi everyone! I'm new to this group. Been struggling with social anxiety lately.",
                    timestamp: new Date(Date.now() - 10800000),
                    isOwn: false
                },
                {
                    id: 'g2',
                    senderId: 'user2',
                    senderName: 'Arjun',
                    content: 'Welcome Maya! This is a safe space. We all understand what you\'re going through.',
                    timestamp: new Date(Date.now() - 10000000),
                    isOwn: false
                },
                {
                    id: 'g3',
                    senderId: 'me',
                    senderName: 'You',
                    content: 'Welcome! I found the group exercises really helpful for my anxiety. Dr. Neha is amazing.',
                    timestamp: new Date(Date.now() - 9000000),
                    isOwn: true
                },
                {
                    id: 'g4',
                    senderId: 'therapist2',
                    senderName: 'Dr. Neha (Facilitator)',
                    content: "Welcome Maya! Let's do a quick check-in: How is everyone feeling today? Rate 1-10.",
                    timestamp: new Date(Date.now() - 8000000),
                    isOwn: false
                },
                {
                    id: 'g5',
                    senderId: 'user3',
                    senderName: 'Priya',
                    content: "I'm at a 6 today. Better than yesterday!",
                    timestamp: new Date(Date.now() - 7500000),
                    isOwn: false
                },
                {
                    id: 'g6',
                    senderId: 'user1',
                    senderName: 'Maya',
                    content: 'Thanks for sharing, really helps!',
                    timestamp: new Date(Date.now() - 7200000),
                    isOwn: false
                }
            ];
        }
        return [];
    };

    const messages = getMockMessages(activeChat);
    const currentChat = chats.find(c => c.id === activeChat);

    const handleSendMessage = () => {
        if (!newMessage.trim()) return;
        // TODO: Send message to API
        console.log('Sending:', newMessage);
        setNewMessage('');
    };

    const formatTime = (date: Date) => {
        const now = Date.now();
        const diff = now - date.getTime();
        const hours = diff / 3600000;

        if (hours < 1) return `${Math.floor(diff / 60000)}m ago`;
        if (hours < 24) return `${Math.floor(hours)}h ago`;
        if (hours < 48) return 'Yesterday';
        return date.toLocaleDateString('en-IN', { month: 'short', day: 'numeric' });
    };

    const directChats = chats.filter(c => c.type === 'direct');
    const groupChats = chats.filter(c => c.type === 'group');

    return (
        <div className="h-screen bg-neutral-50 flex">
            {/* Sidebar */}
            <div className="w-80 bg-white border-r border-neutral-200 flex flex-col">
                {/* Sidebar Header */}
                <div className="p-4 border-b border-neutral-200">
                    <h2 className="text-2xl font-heading text-neutral-900 mb-4">Messages</h2>
                    <div className="relative">
                        <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-neutral-400" />
                        <input
                            type="text"
                            placeholder="Search conversations..."
                            className="w-full pl-10 pr-4 py-2 bg-neutral-50 border border-neutral-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 text-sm"
                        />
                    </div>
                </div>

                {/* Chat List */}
                <div className="flex-1 overflow-y-auto">
                    {/* Therapists Section */}
                    <div className="p-4">
                        <h3 className="text-xs font-semibold text-neutral-500 uppercase mb-3 flex items-center gap-2">
                            <User className="w-4 h-4" />
                            Therapists
                        </h3>
                        <div className="space-y-1">
                            {directChats.map(chat => (
                                <button
                                    key={chat.id}
                                    onClick={() => setActiveChat(chat.id)}
                                    className={`w-full p-3 rounded-lg transition-colors text-left ${activeChat === chat.id
                                        ? 'bg-primary-50 border border-primary-200'
                                        : 'hover:bg-neutral-50'
                                        }`}
                                >
                                    <div className="flex items-start gap-3">
                                        <img
                                            src={chat.avatar}
                                            alt={chat.name}
                                            className="w-10 h-10 rounded-full"
                                        />
                                        <div className="flex-1 min-w-0">
                                            <div className="flex items-start justify-between mb-1">
                                                <h4 className="font-semibold text-neutral-900 text-sm truncate">
                                                    {chat.name}
                                                </h4>
                                                <span className="text-xs text-neutral-500">
                                                    {formatTime(chat.lastMessageTime)}
                                                </span>
                                            </div>
                                            <div className="flex items-center justify-between">
                                                <p className="text-xs text-neutral-600 truncate">
                                                    {chat.lastMessage}
                                                </p>
                                                {chat.unreadCount > 0 && (
                                                    <span className="ml-2 px-2 py-0.5 bg-primary-600 text-white text-xs rounded-full">
                                                        {chat.unreadCount}
                                                    </span>
                                                )}
                                            </div>
                                        </div>
                                    </div>
                                </button>
                            ))}
                        </div>
                    </div>

                    {/* Support Groups Section */}
                    <div className="p-4 border-t border-neutral-100">
                        <h3 className="text-xs font-semibold text-neutral-500 uppercase mb-3 flex items-center gap-2">
                            <Users className="w-4 h-4" />
                            Support Groups
                        </h3>
                        <div className="space-y-1">
                            {groupChats.map(chat => (
                                <button
                                    key={chat.id}
                                    onClick={() => setActiveChat(chat.id)}
                                    className={`w-full p-3 rounded-lg transition-colors text-left ${activeChat === chat.id
                                        ? 'bg-secondary-50 border border-secondary-200'
                                        : 'hover:bg-neutral-50'
                                        }`}
                                >
                                    <div className="flex items-start gap-3">
                                        <div className="w-10 h-10 bg-secondary-100 rounded-full flex items-center justify-center">
                                            <Users className="w-5 h-5 text-secondary-600" />
                                        </div>
                                        <div className="flex-1 min-w-0">
                                            <div className="flex items-start justify-between mb-1">
                                                <h4 className="font-semibold text-neutral-900 text-sm truncate">
                                                    {chat.name}
                                                </h4>
                                                <span className="text-xs text-neutral-500">
                                                    {formatTime(chat.lastMessageTime)}
                                                </span>
                                            </div>
                                            <p className="text-xs text-neutral-600 truncate mb-1">
                                                {chat.lastMessage}
                                            </p>
                                            <div className="flex items-center justify-between">
                                                <span className="text-xs text-neutral-500">
                                                    {chat.participants?.length} members
                                                </span>
                                                {chat.unreadCount > 0 && (
                                                    <span className="px-2 py-0.5 bg-secondary-600 text-white text-xs rounded-full">
                                                        {chat.unreadCount}
                                                    </span>
                                                )}
                                            </div>
                                        </div>
                                    </div>
                                </button>
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            {/* Chat Area */}
            <div className="flex-1 flex flex-col">
                {currentChat ? (
                    <>
                        {/* Chat Header */}
                        <div className="p-4 bg-white border-b border-neutral-200">
                            <div className="flex items-center justify-between">
                                <div className="flex items-center gap-3">
                                    {currentChat.type === 'direct' ? (
                                        <img
                                            src={currentChat.avatar}
                                            alt={currentChat.name}
                                            className="w-10 h-10 rounded-full"
                                        />
                                    ) : (
                                        <div className="w-10 h-10 bg-secondary-100 rounded-full flex items-center justify-center">
                                            <Users className="w-5 h-5 text-secondary-600" />
                                        </div>
                                    )}
                                    <div>
                                        <h3 className="font-semibold text-neutral-900">{currentChat.name}</h3>
                                        {currentChat.type === 'group' && (
                                            <p className="text-sm text-neutral-600">
                                                {currentChat.participants?.join(', ')}
                                            </p>
                                        )}
                                    </div>
                                </div>
                                <button className="p-2 hover:bg-neutral-100 rounded-lg transition-colors">
                                    <MoreVertical className="w-5 h-5 text-neutral-600" />
                                </button>
                            </div>
                        </div>

                        {/* Messages */}
                        <div className="flex-1 overflow-y-auto p-6 space-y-4 bg-neutral-50">
                            {messages.map((message) => (
                                <motion.div
                                    key={message.id}
                                    className={`flex ${message.isOwn ? 'justify-end' : 'justify-start'}`}
                                    initial={{ opacity: 0, y: 10 }}
                                    animate={{ opacity: 1, y: 0 }}
                                >
                                    <div className={`max-w-lg ${message.isOwn ? 'text-right' : 'text-left'}`}>
                                        {!message.isOwn && currentChat.type === 'group' && (
                                            <p className="text-xs text-neutral-500 mb-1 px-1">
                                                {message.senderName}
                                            </p>
                                        )}
                                        <div
                                            className={`inline-block px-4 py-3 rounded-2xl ${message.isOwn
                                                ? 'bg-primary-600 text-white'
                                                : 'bg-white text-neutral-900 border border-neutral-200'
                                                }`}
                                        >
                                            <p className="text-sm">{message.content}</p>
                                        </div>
                                        <p className="text-xs text-neutral-500 mt-1 px-1">
                                            {message.timestamp.toLocaleTimeString('en-IN', {
                                                hour: '2-digit',
                                                minute: '2-digit'
                                            })}
                                        </p>
                                    </div>
                                </motion.div>
                            ))}
                        </div>

                        {/* Message Input */}
                        <div className="p-4 bg-white border-t border-neutral-200">
                            <div className="flex items-end gap-2">
                                <button className="p-2 hover:bg-neutral-100 rounded-lg transition-colors">
                                    <Smile className="w-5 h-5 text-neutral-600" />
                                </button>
                                <textarea
                                    value={newMessage}
                                    onChange={(e) => setNewMessage(e.target.value)}
                                    onKeyPress={(e) => {
                                        if (e.key === 'Enter' && !e.shiftKey) {
                                            e.preventDefault();
                                            handleSendMessage();
                                        }
                                    }}
                                    placeholder="Type a message..."
                                    rows={1}
                                    className="flex-1 px-4 py-3 bg-neutral-50 border border-neutral-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 resize-none"
                                />
                                <button
                                    onClick={handleSendMessage}
                                    disabled={!newMessage.trim()}
                                    className={`p-3 rounded-lg transition-colors ${newMessage.trim()
                                        ? 'bg-primary-600 text-white hover:bg-primary-700'
                                        : 'bg-neutral-200 text-neutral-400 cursor-not-allowed'
                                        }`}
                                >
                                    <Send className="w-5 h-5" />
                                </button>
                            </div>
                        </div>
                    </>
                ) : (
                    <div className="flex-1 flex items-center justify-center bg-neutral-50">
                        <div className="text-center">
                            <Users className="w-16 h-16 text-neutral-300 mx-auto mb-4" />
                            <p className="text-neutral-600">Select a conversation to start messaging</p>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

import React, { useState, useEffect } from 'react';
import { XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, AreaChart, Area } from 'recharts';
import { Calendar, TrendingUp, Activity, Plus } from 'lucide-react';
import api from '../services/api';

const moodScale: Record<string, number> = {
    'Great': 5,
    'Good': 4,
    'Okay': 3,
    'Low': 2,
    'Bad': 1
};

const reverseMoodScale: Record<number, string> = {
    5: 'Great',
    4: 'Good',
    3: 'Okay',
    2: 'Low',
    1: 'Bad'
};

export const ProgressPage: React.FC = () => {
    const [moodHistory, setMoodHistory] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [showLogModal, setShowLogModal] = useState(false);
    const [newMood, setNewMood] = useState('Good');
    const [newNote, setNewNote] = useState('');

    const fetchMoods = async () => {
        try {
            const data = await api.getMoodHistory();
            // Process data for charts
            const processed = data.map((entry: any) => ({
                ...entry,
                score: moodScale[entry.mood] || 3,
                date: new Date(entry.timestamp).toLocaleDateString(),
                fullDate: new Date(entry.timestamp)
            })).sort((a: any, b: any) => a.fullDate.getTime() - b.fullDate.getTime());

            setMoodHistory(processed);
        } catch (error) {
            console.error('Failed to fetch mood history', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchMoods();
    }, []);

    const handleLogMood = async () => {
        try {
            await api.logMood({ mood: newMood, note: newNote });
            setShowLogModal(false);
            setNewNote('');
            fetchMoods(); // Refresh chart
        } catch (error) {
            console.error('Failed to log mood', error);
        }
    };

    // Calculate Stats
    const averageMood = moodHistory.length > 0
        ? (moodHistory.reduce((acc, curr) => acc + curr.score, 0) / moodHistory.length).toFixed(1)
        : 'N/A';

    const streak = 3; // Placeholder for streak logic

    return (
        <div className="p-8 max-w-7xl mx-auto space-y-8">
            <header className="flex justify-between items-center mb-8">
                <div>
                    <h1 className="text-3xl font-bold text-slate-900">Your Progress</h1>
                    <p className="text-slate-500 mt-1">Track your emotional journey and milestones.</p>
                </div>
                <button
                    onClick={() => setShowLogModal(true)}
                    className="flex items-center gap-2 bg-primary-600 text-white px-5 py-2.5 rounded-lg hover:bg-primary-700 transition-colors shadow-sm"
                >
                    <Plus className="w-5 h-5" />
                    Log Today's Mood
                </button>
            </header>

            {/* Stats Grid */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div className="bg-white p-6 rounded-2xl shadow-sm border border-slate-100 flex items-center gap-4">
                    <div className="p-3 bg-blue-50 text-blue-600 rounded-xl">
                        <TrendingUp className="w-6 h-6" />
                    </div>
                    <div>
                        <p className="text-sm text-slate-500 font-medium">Average Mood</p>
                        <p className="text-2xl font-bold text-slate-900">{averageMood} / 5.0</p>
                    </div>
                </div>
                <div className="bg-white p-6 rounded-2xl shadow-sm border border-slate-100 flex items-center gap-4">
                    <div className="p-3 bg-green-50 text-green-600 rounded-xl">
                        <Activity className="w-6 h-6" />
                    </div>
                    <div>
                        <p className="text-sm text-slate-500 font-medium">Total Entries</p>
                        <p className="text-2xl font-bold text-slate-900">{moodHistory.length}</p>
                    </div>
                </div>
                <div className="bg-white p-6 rounded-2xl shadow-sm border border-slate-100 flex items-center gap-4">
                    <div className="p-3 bg-purple-50 text-purple-600 rounded-xl">
                        <Calendar className="w-6 h-6" />
                    </div>
                    <div>
                        <p className="text-sm text-slate-500 font-medium">Current Streak</p>
                        <p className="text-2xl font-bold text-slate-900">{streak} Days</p>
                    </div>
                </div>
            </div>

            {/* Main Chart */}
            <div className="bg-white p-8 rounded-3xl shadow-sm border border-slate-100">
                <h3 className="text-xl font-bold text-slate-800 mb-6">Mood Timeline</h3>
                <div className="h-[400px] w-full">
                    {loading ? (
                        <div className="h-full flex items-center justify-center text-slate-400">Loading chart...</div>
                    ) : moodHistory.length > 0 ? (
                        <ResponsiveContainer width="100%" height="100%">
                            <AreaChart data={moodHistory} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                                <defs>
                                    <linearGradient id="colorMood" x1="0" y1="0" x2="0" y2="1">
                                        <stop offset="5%" stopColor="#0ea5e9" stopOpacity={0.2} />
                                        <stop offset="95%" stopColor="#0ea5e9" stopOpacity={0} />
                                    </linearGradient>
                                </defs>
                                <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#E2E8F0" />
                                <XAxis
                                    dataKey="date"
                                    axisLine={false}
                                    tickLine={false}
                                    tick={{ fill: '#64748B', fontSize: 12 }}
                                    dy={10}
                                />
                                <YAxis
                                    domain={[1, 5]}
                                    ticks={[1, 2, 3, 4, 5]}
                                    tickFormatter={(value) => reverseMoodScale[value]}
                                    axisLine={false}
                                    tickLine={false}
                                    tick={{ fill: '#64748B', fontSize: 12 }}
                                    width={60}
                                />
                                <Tooltip
                                    contentStyle={{ borderRadius: '12px', border: 'none', boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)' }}
                                />
                                <Area
                                    type="monotone"
                                    dataKey="score"
                                    stroke="#0ea5e9"
                                    strokeWidth={3}
                                    fillOpacity={1}
                                    fill="url(#colorMood)"
                                />
                            </AreaChart>
                        </ResponsiveContainer>
                    ) : (
                        <div className="h-full flex flex-col items-center justify-center text-slate-400">
                            <Activity className="w-12 h-12 mb-4 opacity-20" />
                            <p>No mood data yet. Start logging to see your progress!</p>
                        </div>
                    )}
                </div>
            </div>

            {/* Recent Logs List */}
            <div className="bg-white p-8 rounded-3xl shadow-sm border border-slate-100">
                <h3 className="text-xl font-bold text-slate-800 mb-6">Recent Journal</h3>
                <div className="space-y-4">
                    {moodHistory.slice().reverse().slice(0, 5).map((entry) => (
                        <div key={entry.id} className="flex items-start gap-4 p-4 rounded-xl hover:bg-slate-50 transition-colors border border-transparent hover:border-slate-100">
                            <div className={`w-2 h-12 rounded-full ${entry.score >= 4 ? 'bg-green-500' :
                                entry.score === 3 ? 'bg-yellow-500' : 'bg-red-500'
                                }`} />
                            <div className="flex-1">
                                <div className="flex justify-between items-start mb-1">
                                    <h4 className="font-semibold text-slate-900">{entry.mood}</h4>
                                    <span className="text-xs text-slate-500">{new Date(entry.timestamp).toLocaleString()}</span>
                                </div>
                                {entry.note && <p className="text-slate-600 text-sm">{entry.note}</p>}
                            </div>
                        </div>
                    ))}
                    {moodHistory.length === 0 && (
                        <p className="text-slate-500 italic">No entries yet.</p>
                    )}
                </div>
            </div>

            {/* Log Modal */}
            {showLogModal && (
                <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4 backdrop-blur-sm">
                    <div className="bg-white rounded-2xl w-full max-w-md p-6 shadow-xl animation-scale-up">
                        <h2 className="text-2xl font-bold text-slate-900 mb-6">How are you feeling?</h2>

                        <div className="grid grid-cols-5 gap-2 mb-6">
                            {Object.keys(moodScale).reverse().map((m) => (
                                <button
                                    key={m}
                                    onClick={() => setNewMood(m)}
                                    className={`flex flex-col items-center p-3 rounded-xl transition-all ${newMood === m
                                        ? 'bg-primary-50 ring-2 ring-primary-500 transform scale-105'
                                        : 'hover:bg-slate-50 text-slate-400 hover:text-slate-600'
                                        }`}
                                >
                                    <div className={`w-8 h-8 rounded-full mb-2 ${m === 'Great' ? 'bg-green-100 text-green-600' :
                                        m === 'Good' ? 'bg-emerald-100 text-emerald-600' :
                                            m === 'Okay' ? 'bg-yellow-100 text-yellow-600' :
                                                m === 'Low' ? 'bg-orange-100 text-orange-600' : 'bg-red-100 text-red-600'
                                        }`} />
                                    <span className="text-xs font-medium">{m}</span>
                                </button>
                            ))}
                        </div>

                        <div className="mb-6">
                            <label className="block text-sm font-medium text-slate-700 mb-2">Add a note (optional)</label>
                            <textarea
                                value={newNote}
                                onChange={(e) => setNewNote(e.target.value)}
                                className="w-full p-3 border border-slate-200 rounded-xl focus:ring-2 focus:ring-primary-500 focus:border-transparent outline-none resize-none h-32"
                                placeholder="What's on your mind?"
                            />
                        </div>

                        <div className="flex gap-3">
                            <button
                                onClick={() => setShowLogModal(false)}
                                className="flex-1 px-4 py-3 text-slate-600 font-medium hover:bg-slate-50 rounded-xl transition-colors"
                            >
                                Cancel
                            </button>
                            <button
                                onClick={handleLogMood}
                                className="flex-1 px-4 py-3 bg-primary-600 text-white font-medium rounded-xl hover:bg-primary-700 transition-colors shadow-lg shadow-primary-600/20"
                            >
                                Save Log
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import { Lock, Mail, User as UserIcon, Loader2 } from 'lucide-react';

export const RegisterPage: React.FC = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState<'patient' | 'therapist'>('patient');
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const { login } = useAuth();
    const navigate = useNavigate();

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setIsLoading(true);

        try {
            const response = await api.post('/auth/register', { name, email, password, role });
            login(response.data.token, response.data.user);
            if (response.data.user.role === 'therapist') {
                navigate('/therapist/dashboard');
            } else {
                navigate('/dashboard');
            }
        } catch (err: any) {
            const errorMessage = err.response?.data?.message
                || err.response?.data?.error
                || (typeof err.response?.data === 'string' ? err.response?.data : null)
                || err.message
                || 'Failed to register';
            console.error('Registration Error:', err);
            setError(errorMessage);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-slate-50">
            <div className="max-w-md w-full space-y-8 p-8 bg-white rounded-xl shadow-lg">
                <div className="text-center">
                    <h2 className="mt-6 text-3xl font-extrabold text-slate-900">
                        Create Account
                    </h2>
                    <p className="mt-2 text-sm text-slate-600">
                        Join our mental health support platform
                    </p>
                </div>

                <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
                    {error && (
                        <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded-lg text-sm">
                            {error}
                        </div>
                    )}

                    <div className="rounded-md shadow-sm -space-y-px">
                        <div className="relative">
                            <UserIcon className="absolute left-3 top-3 h-5 w-5 text-slate-400" />
                            <input
                                type="text"
                                required
                                className="appearance-none rounded-t-md relative block w-full px-10 py-3 border border-slate-300 placeholder-slate-500 text-slate-900 focus:outline-none focus:ring-primary-500 focus:border-primary-500 focus:z-10 sm:text-sm"
                                placeholder="Full Name"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>
                        <div className="relative">
                            <Mail className="absolute left-3 top-3 h-5 w-5 text-slate-400" />
                            <input
                                type="email"
                                required
                                className="appearance-none relative block w-full px-10 py-3 border border-slate-300 placeholder-slate-500 text-slate-900 focus:outline-none focus:ring-primary-500 focus:border-primary-500 focus:z-10 sm:text-sm border-t-0"
                                placeholder="Email address"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                        </div>
                        <div className="relative">
                            <Lock className="absolute left-3 top-3 h-5 w-5 text-slate-400" />
                            <input
                                type="password"
                                required
                                className="appearance-none rounded-b-md relative block w-full px-10 py-3 border border-slate-300 placeholder-slate-500 text-slate-900 focus:outline-none focus:ring-primary-500 focus:border-primary-500 focus:z-10 sm:text-sm border-t-0"
                                placeholder="Password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </div>
                    </div>

                    <div className="flex items-center justify-center space-x-4">
                        <label className="flex items-center">
                            <input
                                type="radio"
                                value="patient"
                                checked={role === 'patient'}
                                onChange={() => setRole('patient')}
                                className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-slate-300"
                            />
                            <span className="ml-2 text-sm text-slate-700">Patient</span>
                        </label>
                        <label className="flex items-center">
                            <input
                                type="radio"
                                value="therapist"
                                checked={role === 'therapist'}
                                onChange={() => setRole('therapist')}
                                className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-slate-300"
                            />
                            <span className="ml-2 text-sm text-slate-700">Therapist</span>
                        </label>
                    </div>

                    <div>
                        <button
                            type="submit"
                            disabled={isLoading}
                            className="group relative w-full flex justify-center py-3 px-4 border border-transparent text-sm font-medium rounded-lg text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                        >
                            {isLoading ? (
                                <Loader2 className="h-5 w-5 animate-spin" />
                            ) : (
                                'Sign up'
                            )}
                        </button>
                    </div>

                    <div className="text-sm text-center">
                        <span className="text-slate-600">Already have an account? </span>
                        <Link to="/login" className="font-medium text-primary-600 hover:text-primary-500">
                            Sign in
                        </Link>
                    </div>
                </form>
            </div>
        </div>
    );
};

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useParams, useNavigate } from 'react-router-dom';
import { Star, Award, MapPin, Video, CheckCircle, Calendar, Clock, DollarSign, ArrowLeft } from 'lucide-react';

interface FeeStructure {
    individual50: number;
    couples75: number;
    consult30: number;
}

interface Therapist {
    id: string;
    name: string;
    photo: string;
    specializations: string[];
    certifications: string[];
    experienceYears: number;
    rating: number;
    reviewCount: number;
    location: string;
    bio: string;
    feeStructure: FeeStructure;
}

interface TimeSlot {
    id: string;
    label: string;
    dateTime: Date;
}

export const TherapistBookingPage: React.FC = () => {
    const { therapistId } = useParams<{ therapistId: string }>();
    const navigate = useNavigate();
    const [selectedSlot, setSelectedSlot] = useState<string | null>(null);
    const [bookingSuccess, setBookingSuccess] = useState(false);

    // Mock therapist data (in real app, fetch by ID)
    const therapist: Therapist = {
        id: therapistId || 't1',
        name: 'Dr. Sarah Mehta',
        photo: 'https://ui-avatars.com/api/?name=Sarah+Mehta&background=3b82f6&color=fff&size=200',
        specializations: ['Anxiety', 'Depression', 'CBT', 'Trauma'],
        certifications: [
            'CBT Certified Therapist',
            'Trauma-Informed Care Specialist',
            'Licensed Clinical Psychologist (LCP)',
            'EMDR Practitioner'
        ],
        experienceYears: 8,
        rating: 4.9,
        reviewCount: 142,
        location: 'Mumbai, India',
        bio: 'I am a licensed clinical psychologist with 8+ years of experience specializing in anxiety, depression, and trauma-informed care. My approach combines evidence-based cognitive behavioral therapy (CBT) with compassionate, personalized support tailored to each client\'s unique needs.\n\nI believe in creating a safe, non-judgmental space where healing and growth can flourish. Whether you\'re struggling with overwhelming anxiety, persistent sadness, relationship challenges, or past trauma, I\'m here to walk alongside you on your journey to wellness.\n\nMy therapeutic style is collaborative, warm, and solution-focused. Together, we\'ll identify your goals, develop practical coping strategies, and work toward lasting positive change.',
        feeStructure: {
            individual50: 2500,
            couples75: 3500,
            consult30: 1500
        }
    };

    // Mock available slots (3 slots as requested)
    const availableSlots: TimeSlot[] = [
        {
            id: 'slot1',
            label: 'Today 3:00 PM',
            dateTime: new Date(new Date().setHours(15, 0, 0, 0))
        },
        {
            id: 'slot2',
            label: 'Tomorrow 10:00 AM',
            dateTime: new Date(new Date().getTime() + 86400000).setHours(10, 0, 0, 0) as any
        },
        {
            id: 'slot3',
            label: 'Tomorrow 2:00 PM',
            dateTime: new Date(new Date().getTime() + 86400000).setHours(14, 0, 0, 0) as any
        }
    ];

    const handleBookSession = () => {
        if (!selectedSlot) return;

        // TODO: API call to create appointment
        console.log('Booking session:', { therapistId, slotId: selectedSlot });

        setBookingSuccess(true);

        // Navigate to appointments after 2 seconds
        setTimeout(() => {
            navigate('/appointments');
        }, 2000);
    };

    const renderStars = (rating: number) => {
        return (
            <div className="flex gap-0.5">
                {[1, 2, 3, 4, 5].map((star) => (
                    <Star
                        key={star}
                        className={`w-5 h-5 ${star <= Math.floor(rating)
                                ? 'fill-yellow-400 text-yellow-400'
                                : 'text-gray-300'
                            }`}
                    />
                ))}
            </div>
        );
    };

    if (bookingSuccess) {
