                    </p>
                    <p className="text-sm text-neutral-500">
                        Redirecting to your appointments...
                    </p>
                </motion.div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-neutral-50 p-6">
            <div className="max-w-5xl mx-auto space-y-6">
