                            <p className="text-neutral-700 leading-relaxed whitespace-pre-line">
                                {therapist.bio}
                            </p>
                        </motion.div>

                        {/* Specializations & Certifications */}
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            <motion.div
